ftp.biggerplanet.com"		//	FTP Directory	}};resource 'STR#' (2, "Vendor") {		//	as defined in VersionEdit from Teknosys	{		"Nathan Lamont",				//	Author		"Bigger Planet",						//	Company		"Brookline, MA USA",	//	Address		"",		//	Phone		"",		//	Fax		"",		//	AOL address		"",		//	CompuServe address		"",		//  ? unused ?		"nathan@biggerplanet.com",	//	Internet eMail					"http://www.biggerplanet.com",	//	Web Site			"ftp://ftp.biggerplanet.com" //	FTP Directory	}};read 'STR ' (1, prodDesc) prodDesc;				//	as defined in VersionEdit from Teknosys